/* Ao contrário do Javascript que seleciona o item e guarda em uma variável, o jQuery faz apontamento para o item que vamos trabalhar, podendo ser elemento, classe, id etc */
jQuery("h1").css("color", "#ff0000")

/* O seletor jQuery pode ser substituido por $. Aliás, este é o método mais popularmente utilizado */
$("h2").css("color", "#aa88ff")